function drawInlineBlock(h) {
  let buttonList = [0, 0, 0, 0, 0, 0, 0].map((item, index) => {
    return h(
      'view',
      {
        styles: {
          height: 20,
          backgroundColor: '#ff6c79',
          borderRadius: 2,
          borderColor: '#fff',
          margin: 2,
          paddingLeft: 10,
          paddingRight: 10,
          display: 'inline-block',
        },
      },
      [
        h(
          'text',
          {
            styles: {
              lineHeight: 16,
              color: '#fff',
              fontSize: 11,
            },
          },
          `查看${index}`
        ),
      ]
    )
  })
  return h(
    'view',
    {
      styles: {
        borderTopWidth: 0.5,
        borderColor: '#ccc',
        paddingTop: 4,
        marginTop: 4,
        textAlign: 'right'
      },
    },
    [
      ...buttonList,
    ]
  )
}
